import React from "react"
import api from "../utils/Api"

function Main({onEditProfile, onAddPlace, onEditAvatar}) {
  const [userName, setUserName] = React.useState('')
  const [userDescription, setUserDescription] = React.useState('')
  const [userAvatar, setUserAvatar] = React.useState('')
  const [cards, setCards] = React.useState([])

  React.useEffect(() => {
      api.getProfileInfo()
      .then(data => {
        setUserName(data.name)
        setUserDescription(data.about)
        setUserAvatar(data.avatar)
      })
    }
  )

  React.useEffect(() => {
    api.getInitialCard()
    .then(data => {
      const newCards = data.map(card => {
        return {
          name: card.name,
          likes: card.likes,
          link: card.link
        };
      });
      setCards(newCards);
    });

    newCards.forEach((card) => {
      return (
        <template id="card">
        <article className="card">
          <button className="card__trash" type="button"></button>
          <img src={card.link} alt={card.name} className="card__img" />
          <div className="card__info">
            <h2 className="card__title">{card.name}</h2>
            <div className="card__like-block">
              <button className="card__heart" type="button"></button>
              <p className="card__like-counter">{card.likes.length}</p>
            </div>
          </div>
        </article>
      </template>
      )
    })
  }, []);

  return (
    <main className="content">
    <section className="profile">
      <div className="profile__avatar-block">
        <img src={userAvatar} className="profile__avatar" alt="Аватар" onClick={onEditAvatar} />
        <div className="profile__avatar-update"></div>
      </div>
      <div className="profile__info">
        <div className="profile__title">
          <h1 className="profile__text-title">{userName}</h1>
          <button className="profile__button-edit" type="button" onClick={onEditProfile}></button>
        </div>
        <p className="profile__text-subtitle">{userDescription}</p>
      </div>
      <button className="profile__button-add" type="button" onClick={onAddPlace}></button>
    </section>
    <section className="elements" aria-label="Места">
    </section>

    <template id="card">
      <article className="card">
        <button className="card__trash" type="button"></button>
        <img src="Null" alt="" className="card__img" />
        <div className="card__info">
          <h2 className="card__title"></h2>
          <div className="card__like-block">
            <button className="card__heart" type="button"></button>
            <p className="card__like-counter"></p>
          </div>
        </div>
      </article>
    </template>
  </main>
  )
}

export default Main
