import { useState } from 'react';
import '../index.css';
import Header from './Header.js';
import Main from './Main.js';
import PopupWithForm from './PopupWithForm';
import Footer from './Footer.js';
import ImagePopup from './ImagePopup';

function App() {
  const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = useState(false);
  const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = useState(false);
  const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] = useState(false);
  const [selectedCard, setSelectedCard] = useState(null);
  const [inputValue, setInputValue] = useState('');

  function handleEditProfileClick() {
    setIsEditProfilePopupOpen(true);
    console.log(isEditProfilePopupOpen)
  }

  function handleAddPlaceClick() {
    setIsAddPlacePopupOpen(true);
  }

  function handleEditAvatarClick() {
    setIsEditAvatarPopupOpen(true);
  }

  function handleCardClick(card) {
    setSelectedCard(card)
  }

  function closeAllPopups() {
    setIsEditProfilePopupOpen(false);
    setIsAddPlacePopupOpen(false);
    setIsEditAvatarPopupOpen(false);
    setSelectedCard(null)
  }

  return (
    <body className="body">
      <div className="page">
        <Header />
        <Main 
          onEditProfile={handleEditProfileClick}
          onAddPlace={handleAddPlaceClick} 
          onEditAvatar={handleEditAvatarClick}
          onCardClick={handleCardClick}/>
        <PopupWithForm 
          name='profile' 
          title='Редактировать профиль' 
          isOpen={isEditProfilePopupOpen} 
          onClose={closeAllPopups}>
          <input 
            id="profile-name" 
            name="name" 
            placeholder="Ваше имя" 
            type="text" 
            value={inputValue}
            onChange={(evt) => {setInputValue(evt.target.value)}}
            className="popup__input" 
            required 
            minLength="2" 
            maxLength="20" />
          <span className="popup__input-error" id="profile-name-text-error"></span>
          <input 
            id="profile-job" 
            name="description" 
            placeholder="Расскажите о себе" 
            type="text" 
            value=""
            className="popup__input" 
            required 
            minLength="2" 
            maxLength="200" />
          <span className="popup__input-error" id="profile-job-text-error"></span>
        </PopupWithForm>
        <PopupWithForm 
          name='place' 
          title='Новое место' 
          isOpen={isAddPlacePopupOpen} 
          onClose={closeAllPopups}>
            <input 
              id="place-name" 
              name="formInput" 
              placeholder="Название" 
              type="text" 
              value="" 
              className="popup__input" 
              required 
              minLength="2" 
              maxLength="200" />
            <span className="popup__input-error" id="place-name-text-error"></span>
            <input 
              id="place-img" 
              name="link" 
              placeholder="Ссылка на картинку" 
              type="url" 
              value=""
              className="popup__input" 
              required />
            <span className="popup__input-error" id="place-img-text-error"></span>
        </PopupWithForm>
        <PopupWithForm 
          name='avatar' 
          title='Обновить аватар' 
          isOpen={isEditAvatarPopupOpen} 
          onClose={closeAllPopups}>
            <input 
              id="avatar-img" 
              name="link" 
              placeholder="Ссылка на картинку" 
              type="url" 
              value=""
              className="popup__input" 
              required />
            <span className="popup__input-error" id="avatar-img-text-error"></span>
        </PopupWithForm>
        <ImagePopup card={selectedCard} onClose={closeAllPopups} />
        <Footer /> 
      </div>
    </body>
  );
}

export default App;
