  //     .then(res => {
  //       console.log(res)
  //     })
  //   }
  // )

  return (
    <main className="content">
    <section className="profile">
      <div className="profile__avatar-block">
        <img src='{userAvatar}' className="profile__avatar" alt="Аватар" onClick={onEditAvatar} />
        <div className="profile__avatar-update"></div>
      </div>
      <div className="profile__info">
        <div className="profile__title">
          <h1 className="profile__text-title">'userName'</h1>
          <button className="profile__button-edit" type="button" onClick={onEditProfile}></button>
        </div>
        <p className="profile__text-subtitle">userDescription</p>
      </div>
      <button className="profile__button-add" type="button" onClick={onAddPlace}></button>
    </section>
    <section className="elements" aria-label="Места">
    </section>

    <template id="card">
      <article className="card">
        <button className="card__trash" type="button"></button>
        <img src="Null" alt="" className="card__img" />
        <div className="card__info">
          <h2 className="card__title"></h2>
          <div className="card__like-block">
            <button className="card__heart" type="button"></button>
            <p className="card__like-counter"></p>
          </div>
        </div>
      </article>
    </template>
  </main>
  )
}

export default Main
